import type { Vec2i } from "../../../../sources/types";
import { CodecType, EncodingProfile } from "../enums";
/**
 * @category Payloads
 */
export type ClientConfig = {
    remote_canvas_size: Vec2i;
    encoder_config: EncoderConfig;
    supported_devices: SupportedDevices;
};
/**
 * @category Payloads
 */
export type EncoderConfig = {
    codec: CodecType;
    profile: EncodingProfile;
    frame_rate: number;
    lossy: boolean;
};
/**
 * @category Payloads
 */
export type SupportedDevices = {
    keyboard: boolean;
    mouse: boolean;
    hololens: boolean;
    gamepad: boolean;
    touchscreen: boolean;
};
/**
 * @category Responses
 */
export type ClientConfigResponse = {
    codec: CodecType;
};
